import React, { useState } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';

const App: React.FC = () => {
    // Function to generate a random hexadecimal number
    const generateRandomHex = () => {
        const randomValue = Math.floor(Math.random() * 256);
        return randomValue.toString(16).padStart(2, '0').toUpperCase();
    };

    const [inputValue, setInputValue] = useState('');
    const [randomHex, setRandomHex] = useState(generateRandomHex());
    const [alertMessage, setAlertMessage] = useState<string | null>(null);
    const [alertStyle, setAlertStyle] = useState('');

    const handleButtonClick = (value: string) => {
        setInputValue(inputValue + value);
    };

    const handleBackspace = () => {
        setInputValue(inputValue.slice(0, -1));
    };

    const handleClear = () => {
        setInputValue('');
    };

    const handleSubmit = () => {
        const inputDecimal = parseInt(inputValue, 16);
        const randomDecimal = parseInt(randomHex, 16);
        if (inputDecimal === randomDecimal) {
            setAlertMessage('Correct!');
            setAlertStyle('alert-success');
        } else {
            setAlertMessage(`Incorrect! Correct number was ${randomDecimal}.`);
            setAlertStyle('alert-danger');
        }
    };


    return (
        <div className="container mt-5">
            <div className="row align-items-center mb-3">
                <div className="col-md-4">
                    <label className="form-label">Random Hex: {randomHex}</label>
                </div>
                <div className="col-md-8">
                    <input type="text" className="form-control" value={inputValue} readOnly />
                </div>
            </div>
            <div className="row">
                {[7, 8, 9, 4, 5, 6, 1, 2, 3].map(num => (
                    <div key={num} className="col-4 mb-2">
                        <button className="btn btn-secondary w-100" onClick={() => handleButtonClick(num.toString())}>
                            {num}
                        </button>
                    </div>
                ))}
                <div className="col-12">
                    <div className="row">
                        <div className="col-3">
                            <button className="btn btn-secondary w-100" onClick={() => handleButtonClick('0')}>
                                0
                            </button>
                        </div>
                        <div className="col-3">
                            <button className="btn btn-warning w-100" onClick={handleBackspace}>Backspace</button>
                        </div>
                        <div className="col-3">
                            <button className="btn btn-danger w-100" onClick={handleClear}>Clear</button>
                        </div>
                        <div className="col-3">
                            <button className="btn btn-primary w-100" onClick={handleSubmit}>Submit</button>
                        </div>
                    </div>
                </div>
            </div>
            {alertMessage && (
                <div className={`alert ${alertStyle} mt-3`} role="alert">
                    {alertMessage}
                </div>
            )}
        </div>
    );
};

export default App;
