import React, { useState } from 'react';
import { FaBackspace, FaTrash, FaPaperPlane } from 'react-icons/fa';
import 'bootstrap/dist/css/bootstrap.min.css';
import './App.css'; // Ensure dark theme CSS is included

const App: React.FC = () => {
    const [inputValue, setInputValue] = useState('');
    const [randomHex, setRandomHex] = useState(() => generateRandomHex());
    const [alertMessage, setAlertMessage] = useState<string | null>(null);
    const [alertStyle, setAlertStyle] = useState('');
    const [prefix, setPrefix] = useState('');
    const [score, setScore] = useState(0);

    function generateRandomHex() {
        const randomValue = Math.floor(Math.random() * 256);
        return randomValue.toString(16).padStart(2, '0').toUpperCase();
    }

    const handleButtonClick = (value: string) => {
        setInputValue(prev => (prev + value).substring(0, 3)); // Limit to 3 characters
    };

    const handleBackspace = () => {
        setInputValue(prev => prev.slice(0, -1));
    };

    const handleClear = () => {
        setInputValue('');
    };

    const handleSubmit = () => {
        const inputDecimal = parseInt(inputValue, 10);
        const randomDecimal = parseInt(randomHex, 16);
        if (inputDecimal === randomDecimal) {
            const newScore = score + 1;
            setScore(newScore);
            if (newScore > 1) setAlertMessage(`Correct! (x${newScore})`);
              else setAlertMessage("Correct!");
            setAlertStyle('alert-success');
        } else {
            setScore(1);
            setAlertMessage(`Incorrect! (${randomDecimal})`);
            setAlertStyle('alert-danger');
        }
        setInputValue('');
        setRandomHex(generateRandomHex());
    };

    const handlePrefixChange = (event: React.ChangeEvent<HTMLInputElement>) => {
        setPrefix(event.target.value);
    };

    return (
        <div className="d-flex align-items-center justify-content-center">
          <div className="container text-white bg-dark p-3">
                <div className="row align-items-center mb-3">
                    <div className="col-auto w-50 heading">
                      <label className="form-label">Hex: {prefix + randomHex}</label>
                    </div>
                    <div className="col-auto w-25 heading">
                      <label className="form-label">Decimal:</label>
                    </div>
                    <div className="w-25">
                        <input type="text" className="form-control" value={inputValue} readOnly style={{ width: '80px' }} />
                    </div>
                </div>
                <div className="row">
                    {['7', '8', '9', '4', '5', '6', '1', '2', '3'].map(num => (
                        <div key={num} className="col-4 p-1 container-btn">
                            <button className="btn btn-secondary w-100"
                                    onClick={() => handleButtonClick(num.toString())}>
                                {num}
                            </button>
                        </div>
                    ))}
                    <div className="col-12 p-1">
                        <div className="row">
                            <div className="col-3 p-1">
                                <button className="btn btn-secondary w-100"
                                        onClick={() => handleButtonClick('0')}>
                                    0
                                </button>
                            </div>
                            <div className="col-3 p-1">
                                <button className="btn btn-warning w-100"
                                        onClick={handleBackspace}>
                                    <FaBackspace />
                                </button>
                            </div>
                            <div className="col-3 p-1">
                                <button className="btn btn-danger w-100"
                                        onClick={handleClear}>
                                    <FaTrash />
                                </button>
                            </div>
                            <div className="col-3 p-1">
                               <button className="btn btn-primary w-100"
                                        onClick={handleSubmit}>
                                    <FaPaperPlane />
                                </button>
                            </div>
                        </div>
                    </div>
                </div>
                {alertMessage && (
                    <div className={`alert ${alertStyle} mt-3`} role="alert">
                        {alertMessage}
                    </div>
                )}
                <div className="row">
                    <div className="col-auto">
                        <label>Prefix:</label>
                    </div>
                    <div className="col-auto">
                        <div className="form-check form-check-inline">
                            <input className="form-check-input" type="radio" name="prefixOptions" id="noPrefix" value="" checked={prefix === ''} onChange={handlePrefixChange} />
                            <label className="form-check-label" htmlFor="noPrefix">
                                None
                            </label>
                        </div>
                        <div className="form-check form-check-inline">
                            <input className="form-check-input" type="radio" name="prefixOptions" id="prefix0x" value="0x" checked={prefix === '0x'} onChange={handlePrefixChange} />
                            <label className="form-check-label" htmlFor="prefix0x">
                                0x
                            </label>
                        </div>
                        <div className="form-check form-check-inline">
                            <input className="form-check-input" type="radio" name="prefixOptions" id="prefixDollar" value="$" checked={prefix === '$'} onChange={handlePrefixChange} />
                            <label className="form-check-label" htmlFor="prefixDollar">
                                $
                            </label>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default App;
