import React, { useState } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import './App.css';

const App: React.FC = () => {
    const [inputValue, setInputValue] = useState('');

    const handleButtonClick = (value: string) => {
        setInputValue(inputValue + value);
    };

    const handleBackspace = () => {
        setInputValue(inputValue.slice(0, -1));
    };

    const handleClear = () => {
        setInputValue('');
    };

    return (
        <div className="container mt-5">
            <input type="text" className="form-control mb-3" value={inputValue} readOnly />
            <div className="row">
                {[...Array(10).keys()].map(num => (
                    <div key={num} className="col-4 mb-2">
                        <button className="btn btn-secondary w-100" onClick={() => handleButtonClick(num.toString())}>
                            {num}
                        </button>
                    </div>
                ))}
            </div>
            <div className="row">
                <div className="col-6">
                    <button className="btn btn-warning w-100" onClick={handleBackspace}>Backspace</button>
                </div>
                <div className="col-6">
                    <button className="btn btn-danger w-100" onClick={handleClear}>Clear</button>
                </div>
            </div>
        </div>
    );
};

export default App;
